import styled from 'styled-components';

export const CartIconContainer = styled.div`
    width: 45px;
    height: 45px;
    position: relative;
    display: flex;
    align-items: center;
    justify-content: center;
    cursor: pointer;
`;

export const ShoppingIcon = styled(ShoppingIcon)`
    width: 24px;
    height: 24px;
`;
